mapper.select:
select count(id)

from operate_log
where 1 = 1

	and(username like '%${keyWord}%' or operate_name like '%${keyWord}%' or operate_content like '%${keyWord}%' or operate_result like '%${keyWord}%')


	and creat_time > #{startTime,jdbcType=TIMESTAMP}


	and creat_time < #{stopTime,jdbcType=TIMESTAMP}

select 

from operate_log
where 1 = 1

	and(username like '%${keyWord}%' or operate_name like '%${keyWord}%' or operate_content like '%${keyWord}%' or operate_result like '%${keyWord}%')


	and creat_time > #{startTime,jdbcType=TIMESTAMP}


	and creat_time < #{stopTime,jdbcType=TIMESTAMP}

order by creat_time desc
limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
